import Head from 'next/head';


function Navbar() {
  return (
    <div>

      <html>
        <body>
          <nav className="navbar">
            <div className="container">
              <a href="#">Hulk</a>
              <a href="#" className="logo is-active">Mavel</a>
              <a href="#">Venom</a>
            </div>
          </nav>
        </body>
      </html>
      <style jsx>{`
        *,
        *::before,
        *::after {
          padding: 0;
          margin: 0;
          box-sizing: border-box;
          transition: all 0.3s ease-in-out;
        }
        html,
        body {
          width: 100%;
          min-height: 100vh;
          background-color: #c4c4c4
        }

        html {
          font-size: 10px;
        }

        body {
          font-family: 'Nunito', -apple-system, BlinkMacSystemFont, "Segoe UI", Oxygen,
            Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;

          font-size: 1.6rem;
          line-height: 1.5;
        }
        .navbar {
          width: 100%;
          height: 8rem;
          background-color: #111;
          color: #ffffff;
          position: fixed;
          left: 0;
          top: 0;
          z-index: 99;
          box-shadow: 0px 6px 8px rgba(0, 0, 0, 0.5);
        }

        .navbar .container {
          height: 100%;
          display: flex;
          align-items: center;
          justify-content: space-evenly;
        }

        .navbar a {
          color: #ffffff;
          text-transform: uppercase;
          letter-spacing: 0.5rem;
          text-align: center;

          height: 100%;
          padding: 2rem;

          display: grid;
          place-content: center;
        }

        .navbar a.is-active {
          border-bottom: 4px solid #ffffff;
        }

        @media (max-width: 768px) {
          .navbar a {
            font-size: 1.4rem;
          }
        }


        // Global CSS
        *,
        *::before,
        *::after {
          padding: 0;
          margin: 0;
          box-sizing: border-box;
          transition: all 0.3s ease-in-out;
        }

        html,
        body {
          width: 100%;
          min-height: 100vh;
          background-color: #c4c4c4
        }

        html {
          font-size: 10px;
        }

        body {
          font-family: 'Nunito', -apple-system, BlinkMacSystemFont, "Segoe UI", Oxygen,
            Ubuntu, Cantarell, "Open Sans", "Helvetica Neue", sans-serif;

          font-size: 1.6rem;
          line-height: 1.5;
        }
        a,
        a[href=""] {
          text-decoration: none;
        }

        .container {
          width: 124rem;
          max-width: 100%;
          margin: 0rem auto;
        }
        @media (max-width: 1280px) {
          .container {
            padding: 0rem 10rem;
          }
        }

        @media (max-width: 768px) {
          .container {
            padding: 0rem 4rem;
          }
        }

      `}
      </style>
    </div>
  )
}

export default Navbar
